import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SlotDetails.css';

function SlotDetails() {
  const [slots, setSlots] = useState([]);
  const [visitors, setVisitors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch slots and visitors data
        const [slotsRes, visitorsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/slots'),
          axios.get('http://localhost:5000/api/previous-logs')
        ]);

        // Pad slots to ensure exactly 10
        const slotsData = slotsRes.data;
        const paddedSlots = Array.from({ length: 10 }, (_, i) => {
          const slot = slotsData.find(s => s.slot_id === i + 1);
          return slot || { slot_id: i + 1, status: 'empty', visitor_id: null };
        });

        setSlots(paddedSlots);
        setVisitors(visitorsRes.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch slot details');
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="container">
      <h2>Slot Details</h2>
      <div className="slot-details-container">
        {slots.map((slot) => {
          const visitor = slot.status === 'occupied' && slot.visitor_id
            ? visitors.find(v => v._id === slot.visitor_id)
            : null;

          return (
            <div key={slot.slot_id} className="slot-wrapper">
              <button
                className={`slot-button ${slot.status === 'occupied' ? 'occupied' : 'empty'}`}
                title={visitor ? 
                  `Name: ${visitor.name || 'Unknown'}\nVehicle: ${visitor.vehicle || 'Unknown'}` : 
                  'None'}
              >
                Slot {slot.slot_id}
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default SlotDetails;
