import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Slots.css';

function Slots() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/slots');
        // Ensure exactly 10 slots by padding with empty slots if needed
        const slots = res.data;
        const paddedSlots = Array.from({ length: 10 }, (_, i) => {
          const slot = slots.find(s => s.slot_id === i + 1);
          return slot || { slot_id: i + 1, status: 'empty', visitor: null };
        });
        setData(paddedSlots);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch slots details');
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="container">
      <h2>Slot Details</h2>
      <div className="slots-container">
        {data.map((slot) => (
          <div key={slot.slot_id} className="slot-wrapper">
            <button
              className={`slot-button ${slot.status === 'occupied' ? 'occupied' : 'empty'}`}
              title={slot.status === 'occupied' && slot.visitor ? 
                `Name: ${slot.visitor.name || 'Unknown'}\nVehicle: ${slot.visitor.vehicle || 'Unknown'}` : 
                'None'}
            >
              Slot {slot.slot_id}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Slots;
